// Libraries
import React, {useContext} from "react";

// App Context
import {AppContext} from "../contexts/AppContext";

// Material UI Style
import {makeStyles} from "@material-ui/core/styles";

// Footer Component
const Footer = () => {
  // Get State
  const state = useContext(AppContext);

  // Custom CSS Classes Style
  const useStyles = makeStyles(theme => ({
      footer: {
        fontFamily: "\"Roboto\", \"Helvetica\", \"Arial\", sans-serif",
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between",
        backgroundColor: state.profile.theme === "light" ? "#ffffff" : "#333333",
        width: "calc(100% - 30px)",
        padding: 15,
        borderTop: state.profile.theme === "light" ? "1px solid rgba(0, 0, 0, 0.12)" : "1px solid rgba(255, 255, 255, 0.12)",
        position: "absolute",
        bottom: 0,
        right: 0,
        [theme.breakpoints.down("sm")]: {
          flexDirection: "column",
          "& > div:first-of-type": {
            marginBottom: 15
          }
        }
      },
      copyright: {
        color: state.profile.theme === "light" ? "#222222" : "#ffffff",
        "& > span:last-of-type": {
          color: state.profile.theme === "light" ? "#3f51b5" : "#7f91ff",
          fontWeight: "bold"
        }
      },
      link: {
        fontSize: 18,
        transition: "0.3s",
        color: state.profile.theme === "light" ? "#222222" : "#ffffff",
        "&:hover": {
          color: state.profile.theme === "light" ? "#3f51b5" : "#7f91ff",
        },
        "& i": {
          padding: "0 8px"
        }
      }
    })
  );

  // Classes Constant
  const classes = useStyles();

  // Component View
  return (
    <footer className={classes.footer}>
      <div className={classes.copyright}>
        Â© <span>{String(new Date().getFullYear())}</span> | <span className="author">Aashish Kumar</span>
      </div>
      <div className="links">
        <a className={classes.link} href="https://github.com/Aashish-cyber" target="_blank" rel="noreferrer">
          <i className="fab fa-github" />
        </a>
        <a className={classes.link} href="https://www.linkedin.com/in/aashish-kumar-0b2427164/" target="_blank" rel="noreferrer">
          <i className="fab fa-linkedin-in" />
        </a>
        <a className={classes.link} href="https://twitter.com/ak_stwt" target="_blank" rel="noreferrer">
          <i className="fab fa-twitter" />
        </a>

        <a className={classes.link} href="https://github.com/Aashish-cyber" target="_blank" rel="noreferrer">
          <i className="fas fa-globe-africa" />
        </a>
        <a className={classes.link} href="ranaaashish000@gmail.com" target="_blank" rel="noreferrer">
          <i className="fas fa-envelope" />
        </a>
      </div>
    </footer>
  );
};

// Export Footer
export default Footer;
